import React, { useState } from 'react';
import { Form, Input, Button, Typography, message } from 'antd';
import { UserOutlined, LockOutlined } from '@ant-design/icons';

const { Title } = Typography;

const Login = ({ onLogin }) => {
  const [loading, setLoading] = useState(false);

  const handleFinish = async (values) => {
    setLoading(true);
    try {
      // 用 FormData 兼容 FastAPI Form 参数
      const formData = new FormData();
      formData.append('username', values.username);
      formData.append('password', values.password);
      const response = await fetch('http://10.201.24.153:8000/api/login', {
        method: 'POST',
        body: formData,
      });
      if (!response.ok) throw new Error('登录失败');
      const data = await response.json();
      localStorage.setItem('user_id', data.user_id);
      message.success('登录成功！');
      onLogin && onLogin();
    } catch (e) {
      message.error('登录失败，请检查用户名和密码！');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{
      minHeight: '100vh',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      background: 'linear-gradient(120deg, #e0eafc 0%, #cfdef3 100%)'
    }}>
      <div style={{
        width: 400,
        padding: 40,
        borderRadius: 24,
        background: 'rgba(255,255,255,0.95)',
        boxShadow: '0 8px 32px rgba(0,0,0,0.12)'
      }}>
        <Title level={2} style={{ textAlign: 'center', marginBottom: 32, color: '#fff', textShadow: '0 2px 8px #0002' }}>登录</Title>
        <Form onFinish={handleFinish}>
          <Form.Item name="username" rules={[{ required: true, message: '请输入用户名！' }]}>
            <Input prefix={<UserOutlined />} placeholder="用户名" size="large" style={{ borderRadius: 16 }} />
          </Form.Item>
          <Form.Item name="password" rules={[{ required: true, message: '请输入密码！' }]}>
            <Input.Password prefix={<LockOutlined />} placeholder="密码" size="large" style={{ borderRadius: 16 }} />
          </Form.Item>
          <Form.Item>
            <Button type="primary" htmlType="submit" block size="large" loading={loading}
              style={{
                borderRadius: 24,
                background: 'linear-gradient(90deg, #36d1c4 0%, #5b86e5 100%)',
                border: 'none',
                fontWeight: 'bold'
              }}>
              登录
            </Button>
          </Form.Item>
        </Form>
      </div>
    </div>
  );
};

export default Login;
