import React, { useState } from 'react';
import { Upload, Button, Card, Typography, message, Spin, Result } from 'antd';
import { UploadOutlined, CheckCircleTwoTone } from '@ant-design/icons';
import axios from 'axios';

const { Title } = Typography;
const API_BASE = process.env.REACT_APP_API_BASE;

const ImageDetect = () => {
  const [loading, setLoading] = useState(false);
  const [detectResult, setDetectResult] = useState(null);

  const handleUpload = async (file) => {
    setLoading(true);
    const formData = new FormData();
    formData.append('file', file);
    try {
      const res = await axios.post(`${API_BASE}/api/detect/image`, formData);
      setDetectResult(res.data);
      message.success('检测完成！');
    } catch (e) {
      message.error('图片检测失败！');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ minHeight: '80vh', display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
      <Card style={{ width: 540, borderRadius: 24, background: 'rgba(34,40,49,0.92)', padding: '40px 32px' }}>
        <Title level={2} style={{ textAlign: 'center', marginBottom: 32, color: '#36d1c4', fontWeight: 700 }}>图片检测</Title>
        <div style={{ display: 'flex', justifyContent: 'center', marginBottom: 32 }}>
          <Upload beforeUpload={handleUpload} showUploadList={false} accept="image/*">
            <Button icon={<UploadOutlined />} loading={loading} size="large" type="primary" style={{ borderRadius: 12, width: 200, fontWeight: 600, fontSize: 18 }}>
              {loading ? '处理中...' : '选择图片'}
            </Button>
          </Upload>
        </div>
        {loading && <div style={{ textAlign: 'center', marginBottom: 24 }}><Spin size="large" tip="正在检测..." /></div>}
        {detectResult && (
          <Result
            icon={<CheckCircleTwoTone twoToneColor="#36d1c4" style={{ fontSize: 48 }} />}
            title={<span style={{ color: '#36d1c4' }}>检测完成！</span>}
            subTitle={<span style={{ color: '#fff' }}>{`检测时间：${detectResult.detected_at}`}</span>}
            extra={
              <Card type="inner" title={<span style={{ color: '#36d1c4' }}>检测结果详情</span>} style={{ marginTop: 16, background: 'rgba(54,209,196,0.08)', borderColor: '#36d1c4', color: '#fff' }}>
                <pre style={{ background: 'none', color: '#333', fontSize: 15, margin: 0, padding: 0, border: 'none' }}>
                  {JSON.stringify(detectResult.detections, null, 2)}
                </pre>
              </Card>
            }
          />
        )}
      </Card>
    </div>
  );
};

export default ImageDetect;
