import React, { useState, useEffect } from 'react';
import { Table, Button, message } from 'antd';
import axios from 'axios';

const API_BASE = process.env.REACT_APP_API_BASE;

const UserManagement = () => {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    setLoading(true);
    try {
      const userId = localStorage.getItem('user_id');
      const response = await axios.get(`${API_BASE}/api/users/${userId}`);
      setUsers([response.data]);
    } catch (error) {
      message.error('获取用户信息失败！');
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async (userId) => {
    try {
      await axios.delete(`${API_BASE}/api/users/${userId}`);
      message.success('用户删除成功！');
      fetchUsers();
    } catch (error) {
      message.error('用户删除失败！');
    }
  };

  const columns = [
    { title: '用户ID', dataIndex: 'user_id', key: 'user_id' },
    { title: '用户名', dataIndex: 'username', key: 'username' },
    { title: '角色', dataIndex: 'role', key: 'role' },
    { title: '创建时间', dataIndex: 'created_at', key: 'created_at' },
    {
      title: '操作',
      key: 'action',
      render: (_, record) => (
        <Button type="danger" onClick={() => handleDelete(record.user_id)}>
          删除
        </Button>
      )
    }
  ];

  return (
    <div style={{ padding: '50px 0' }}>
      <h2>用户管理</h2>
      <Table columns={columns} dataSource={users} loading={loading} rowKey="user_id" />
    </div>
  );
};

export default UserManagement;
