import React, { useState } from 'react';
import { Upload, Button, message } from 'antd';
import { UploadOutlined } from '@ant-design/icons';
import axios from 'axios';

const VideoUpload = () => {
  const [loading, setLoading] = useState(false);
  const [detectResult, setDetectResult] = useState(null);

  const handleUpload = async (file) => {
    setLoading(true);
    const formData = new FormData();
    formData.append('file', file);
    const userId = localStorage.getItem('user_id');
    formData.append('user_id', userId);

    try {
      // 1. 上传视频
      const uploadRes = await axios.post('http://10.201.24.153:8000/api/videos/upload', formData);
      message.success('视频上传成功，开始检测...');
      const videoId = uploadRes.data.video_id;

      // 2. 调用检测接口
      const detectRes = await axios.post('http://10.201.24.153:8000/api/detect/video', null, {
        params: { video_id: videoId }
      });
      message.success('检测完成！');

      // 3. 拉取检测结果详情
      const resultId = detectRes.data.result_id;
      const resultRes = await axios.get(`http://10.201.24.153:8000/api/detection_results/${resultId}`);
      setDetectResult(resultRes.data);
      console.log('检测结果详情', resultRes.data);
      // 你可以在这里弹窗展示，或跳转到检测结果页面
    } catch (error) {
      message.error('视频上传或检测失败，请重试！');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ maxWidth: 300, margin: '0 auto', padding: '50px 0' }}>
      <h2>视频上传</h2>
      <Upload beforeUpload={handleUpload} showUploadList={false}>
        <Button icon={<UploadOutlined />} loading={loading} block>
          选择视频
        </Button>
      </Upload>
      {detectResult && (
        <div>
          <h3>检测结果详情</h3>
          <pre>{JSON.stringify(detectResult, null, 2)}</pre>
        </div>
      )}
    </div>
  );
};

export default VideoUpload;
